<?php/* --- googlemaps ---- */include(plugin_dir_path(__FILE__) . 'tinyMCE.php');add_shortcode('apt_shortcode', 'apt_shortcode');function apt_shortcode($atts, $content = null) {    extract(shortcode_atts(array(        'width' => '100',        'height' => '100',        'src' => ''                    ), $atts));    return '<iframe width="' . $width . '" height="' . $height . '" frameborder="0" scrolling="no" marginheight="0" marginwidth="0" src="' . $src . '&amp;output=embed"></iframe>';}