<?php// Add admin menu page to the menuadd_action('admin_menu', 'leadcapture_menu');add_action('admin_init', 'leadcapture_jquery');add_action('admin_menu', 'leadcapture_style');function form_color_picker_function() {    wp_enqueue_style( 'wp-color-picker' );}add_action('wp_head', 'form_color_picker_function', 10);//Default WordPress jQueryfunction leadcapture_scripts() {    wp_enqueue_script('jquery');}add_action('wp_enqueue_scripts', 'leadcapture_scripts');// Add scripts and stylesheetfunction leadcapture_style() {    wp_enqueue_style( 'wp-color-picker' );    wp_enqueue_style('leadcapture_custom_style', CSS_PLUGIN_URL . 'leads-custom-form.css');    wp_enqueue_style('leadcapture_leads_style', CSS_PLUGIN_URL . 'leads.css');}function leadcapture_jquery() {    if (isset($_GET['page'])) {        if ($_GET['page'] == 'leadcapture') {            //wp_enqueue_script('leadcapture_min_jquery', JQUERY_PLUGIN_URL . 'jquery-1.2.6.min.js');            wp_enqueue_script('leadcapture_personalized_jquery', JQUERY_PLUGIN_URL . 'jquery-ui-personalized-1.5.2.packed.js');            wp_enqueue_script('leadcapture_sprinkle_jquery', JQUERY_PLUGIN_URL . 'sprinkle.js');            //wp_enqueue_script('leadcapture_jscolor_jquery', JQUERY_PLUGIN_URL . 'jscolor.js');            wp_enqueue_style( 'wp-color-picker' );            wp_enqueue_script( 'leadcapture-iris-color-picker', JQUERY_PLUGIN_URL. 'iris.js' , array( 'wp-color-picker' ), false, true );        }            }}function leadcapture_menu() {    if (function_exists('add_menu_page')) {        /** add_menu_page( $page_title, $menu_title, $capability, $menu_slug, $function, $icon_url, $position );          * add_submenu_page( $parent_slug, $page_title, $menu_title, $capability, $menu_slug, $function);         * $page_title=write on page title         * $menu_title= Menu name on display Dashboard         * $capability= user Level Capability in access point top capability is 10 and low is 0.         * $menu_slug=The slug name to refer to this menu by (should be unique for this menu)         * $function= new function creat on dashboard.         * $icon_url=icon url         * $position=position menu on dashboard.         * 2 Dashboard,4 Separator, 5 Posts, 10 Media, 15 Links, 20 Pages, 25 Comments, 59 Separator, 60 Appearance, 65 Plugins, 70 Users,         * 75 Tools, 80 Settings, 99 Separator         */        add_menu_page(__('LeadUp', 'leadcapture'), __('LeadUp', 'leadcapture'), 'manage_options', 'leadcapture', 'dynamic_create_form', IMG_PLUGIN_URL . 'lead-icon.png', 66);        add_submenu_page('leadcapture', __('LeadUp Data', 'leadcapture'), __('LeadUp Data', 'leadcapture'), 'manage_options', 'leadsdata', 'leads_capture_data');        remove_submenu_page('aptservice', 'aptservice');    }}