<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml">    <head>        <title>InkThemes LeadCapture Shortcode</title>        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />        <script language="javascript" type="text/javascript" src="../../../../wp-includes/js/tinymce/tiny_mce_popup.js"></script>        <script language="javascript" type="text/javascript" src="../../../../wp-includes/js/tinymce/utils/mctabs.js"></script>        <script language="javascript" type="text/javascript" src="../../../../wp-includes/js/tinymce/utils/form_utils.js"></script>        <script language="javascript" type="text/javascript" src="../../../../wp-includes/js/jquery/jquery.js?ver=1.4.2"></script>        <script language="javascript" type="text/javascript">            function wpscmagic_showForm() {                tinyMCEPopup.resizeToInnerSize();                var gmapshortcode = tinyMCE.activeEditor.selection.getContent();                if (gmapshortcode != '') {                    document.getElementById('leadcapture').value = gmapshortcode;                }            }            function leads_short() {                var shortcodeMap;                var gmap_Content = document.getElementById('inkaptTab');                if (gmap_Content.className.indexOf('current') != -1) {                    var gmapshortcode = tinyMCE.activeEditor.selection.getContent();                    if (gmap_Content != '') {                        shortcodeMap = '[ink-leadcapture]';                    } else {                        alert('Opps!');                    }                    // insert shortcode in editor                    if (window.tinyMCE) {                        tinymce.activeEditor.execCommand('mceInsertContent', false, shortcodeMap);//                        window.tinyMCE.execInstanceCommand('content', 'mceInsertContent', false, shortcodeMap);//                        tinyMCEPopup.editor.execCommand('mceRepaint');                        tinyMCEPopup.close();                    }                    return;                }            }        </script>        <base target="_self" />        <style type="text/css">            label span {color: #f00; }            .image{ font:30px;                     margin-top:10px;                    margin-left:10px;            }        </style>    </head>    <body onload="wpscmagic_showForm();">        <form name="wpscmagic_sc_form" action="#">            <div class="panel_wrapper" style="height:100px;">                <div id="inkaptTab" class="panel current" style="height:100px;">                    <fieldset >                                <legend>InkThemes LeadCapture Shortcode</legend><br />                          <em style="font-size: 13px; color: #999999; margin-left:40px; padding: 5px 0 0 0;"><strong></strong></em>                        <table border="0" cellpadding="4" cellspacing="0">                                <tr>                                   <td nowrap="nowrap" style="vertical-align: text-top;"><label for="googlemaps_src">LeadCapture Shortcode:</label></td>                                <td>                                                        [ink-leadcapture]</textarea><br />                                 </td>                                              </tr>                            </table>                     </fieldset>                    <div class="image"></div>	                </div><!-- googlemapsTab -->            </div><!-- panel_wrapper -->            <div class="mceActionPanel">                <div style="float: left;">                    <input type="button" id="cancel" name="cancel" value="Close" onclick="tinyMCEPopup.close();" />                </div>                <div style="float: right">                    <input type="submit" id="insert" name="insert" value="Insert" onclick="leads_short();" />                </div>            </div>        </form>    </body></html>