<?phpclass LeadCaptureWidget extends WP_Widget {    function __construct() {        $params = array(            'name' => __('LeadUp Widget', 'leadcapture'),            'description' => __('Just drag and drop the widget to LeadUp plugin in the page', 'leadcapture')        );        parent::__construct('LeadCaptureWidget', '', $params);    }    public function widget($args, $instance) {        extract($args);        $title = apply_filters('widget_title', '');        echo $before_widget;        if ($title)            echo $before_title . $title . $after_title;        $leadcapt_widget = do_shortcode('[ink-leadcapture]');        echo $leadcapt_widget;        echo $after_widget;    }}//add_action('widgets_init', create_function('', 'return register_widget("LeadCaptureWidget");'));function leadUp_register_widget() {   register_widget( 'LeadCaptureWidget' );}add_action( 'widgets_init', 'leadUp_register_widget' );